import React, { useState, useEffect } from 'react';

function App() {
  const [message, setMessage] = useState('');
  const [chatLog, setChatLog] = useState([]);
  const [user, setUser] = useState('User'); // Set default name to 'User'

  // ðŸ” Load history on page load
  useEffect(() => {
    // Load the saved username from localStorage (if any)
    const savedUser = localStorage.getItem('chatUser');
    if (savedUser) {
      setUser(savedUser);
    } else {
      // If no user is saved, use default name 'User'
      setUser('User');
      localStorage.setItem('chatUser', 'User');
    }
  
    // fetch chat history
    fetch('http://localhost:8000/history')
      .then(res => res.json())
      .then(data => setChatLog(data))
      .catch(err => console.error('Failed to fetch chat history:', err));
  }, []);

  const sendMessage = async () => {
    if (!message.trim()) return;

    const newChat = { sender: 'user', text: message };
    setChatLog([...chatLog, newChat]);
    setMessage('');

    try {
      const res = await fetch('http://localhost:8000/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ message, user }),
      });

      const data = await res.json();
      const botReply = { sender: 'bot', text: data.reply };
      setChatLog((prev) => [...prev, botReply]);
    } catch (error) {
      console.error('Error talking to chatbot:', error);
    }
  };

  return (
    <div style={{ maxWidth: '600px', margin: 'auto', padding: '20px' }}>
      <h2>Welcome to the AI Chatbot</h2>
      <div style={{ border: '1px solid #ccc', padding: '10px', minHeight: '300px' }}>
        {chatLog.map((msg, i) => (
          <div
            key={i}
            style={{
              margin: '10px 0',
              textAlign: msg.sender === 'user' ? 'right' : 'left',
              backgroundColor: msg.sender === 'user' ? '#e1f5fe' : '#fff3e0',
              padding: '10px',
              borderRadius: '10px',
              maxWidth: '80%',
              alignSelf: msg.sender === 'user' ? 'flex-end' : 'flex-start',
            }}
          >
            <div><strong>{msg.sender === 'user' ? user : 'Bot'}</strong></div>
            <div>{msg.text}</div>
            {/* Removed the Invalid Date display */}
          </div>
        ))}
      </div>

      <div style={{ marginTop: '10px' }}>
        <input
          type="text"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          style={{ width: '80%', padding: '10px' }}
          onKeyPress={(e) => { if (e.key === 'Enter') sendMessage(); }}
        />
        <button onClick={sendMessage} style={{ padding: '10px 20px', marginLeft: '10px' }}>
          Send
        </button>
        <button
          onClick={async () => {
            await fetch('http://localhost:8000/clear', { method: 'DELETE' });
            setChatLog([]);
          }}
          style={{ marginLeft: '10px', padding: '10px' }}
        >
          Clear Chat
        </button>
      </div>
    </div>
  );
}

export default App;
